class article{constructor(e,n){this.headline=e,this.body=n}}const myFrom=document.querySelector("#my-form");function appendArt(e){document.querySelector("#articles").innerHTML+=`\n    <li>\n        <div">\n            <h4>${e.headline}</h4>\n        </div>\n        <section>\n            <p>${e.body}</p>\n        </section>\n    </li>\n    `}myFrom.addEventListener("submit",(e=>{e.preventDefault(),console.log("work");const n=document.querySelector("#headline-input"),t=document.querySelector("#article-body-input");""===n.value||""===t.value?alert("enter all fields!"):(newArticle=new article(n.value,t.value),appendArt(newArticle),n.value="",t.value="")}));