const xhr=new XMLHttpRequest,fileSource={dashbord:"pages/dashboard.html",setting:"pages/setting.html",help:"pages/help.html",aboutUs:"pages/aboutUs.html"},msg=document.querySelector("#msg"),fileCatcher=document.querySelector("#file-catcher");function changeSlide(e){xhr.open("GET",e,!0),xhr.onload=function(){return 200!==xhr.status?msg.textContent="Something Went wrong!":200===xhr.status?fileCatcher.innerHTML=xhr.response:void 0},xhr.send()}fileCatcher.addEventListener("click",e=>{e.target.classList.contains("btn-act")&&(e.target.textContent.includes("Setting")?changeSlide(fileSource.setting):e.target.textContent.includes("Help")?changeSlide(fileSource.help):e.target.textContent.includes("About us")&&changeSlide(fileSource.aboutUs))}),changeSlide(fileSource.dashbord);